settings.request_button_css:
.quotify-request-quote-btn { 
    display: inline-block;
    padding: 10px 20px;
    font-size: 16px;
    font-weight: bold;
    text-align: center;
    text-decoration: none;
    background-color: #333333;
    color: #FFFFFF;
    border: 2px solid #000000;
    border-radius: 5px;
    cursor: pointer;
}

