settings.custom_liquid:
{% if product.description != blank %}
  {% assign content = product.description %}

  <!-- Split content by first occurrence of <h2> -->
  {% assign content_sections = content | split: '<h2>' %}

  <div class="accordion-wrapper">
    <div class="accordion-list">

      <!-- Check for content before the first <h2> tag -->
      {% assign intro_content = content_sections[0] %}
      {% if intro_content != blank %}
        <div class="accordion-item">
          <div class="accordion-title">
            <h2>Description</h2>
            <span class="plus">+</span>
            <span class="minus" style="display: none;">-</span>
          </div>
          <div class="accordion-content" style="display: none;">
            {{ intro_content | raw }}
          </div>
        </div>
      {% endif %}

      <!-- Loop through the remaining sections split by <h2> tags -->
      {% for section in content_sections offset:1 %}
        {% if section contains "</h2>" %}
          {% assign section_parts = section | split: '</h2>' %}
          {% assign title = section_parts[0] %}
          {% assign section_content = section_parts[1] %}

          {% if section_content != blank %}
            <div class="accordion-item">
              <div class="accordion-title">
                <h4>{{ title | strip_html | escape }}</h4>
                <span class="plus">+</span>
                <span class="minus" style="display: none;">-</span>
              </div>
              <div class="accordion-content" style="display: none;">
                {{ section_content | raw }}
              </div>
            </div>
          {% endif %}
        {% endif %}
      {% endfor %}

    </div>
  </div>

{% else %}
  {% assign content = product.metafields.global.new_description.value %}
  {% if content != blank %}
    {% assign content_sections = content | split: '<h2>' %}

    <div class="accordion-wrapper">
      <div class="accordion-list">

        <!-- Check for content before the first <h2> tag in metafield content -->
        {% assign intro_content = content_sections[0] %}
        {% if intro_content != blank %}
          <div class="accordion-item">
            <div class="accordion-title">
              <h2>Description</h2>
              <span class="plus">+</span>
              <span class="minus" style="display: none;">-</span>
            </div>
            <div class="accordion-content" style="display: none;">
              {{ intro_content | raw }}
            </div>
          </div>
        {% endif %}

        {% for section in content_sections offset:1 %}
          {% if section contains "</h2>" %}
            {% assign section_parts = section | split: '</h2>' %}
            {% assign title = section_parts[0] %}
            {% assign section_content = section_parts[1] %}

            {% if section_content != blank %}
              <div class="accordion-item">
                <div class="accordion-title">
                  <h4>{{ title | strip_html | escape }}</h4>
                  <span class="plus">+</span>
                  <span class="minus" style="display: none;">-</span>
                </div>
                <div class="accordion-content" style="display: none;">
                  {{ section_content | raw }}
                </div>
              </div>
            {% endif %}
          {% endif %}
        {% endfor %}

      </div>
    </div>
  {% endif %}
{% endif %}

