settings.custom_liquid:
{% if product.description != blank %}
  {% assign content = product.description %}

  <!-- Split content by first occurrence of <h2> -->
  {% assign content_sections = content | split: '<h2>' %}

  <div class="accordion-wrapper">
    <div class="accordion-list">
      {% assign last_title = "Description" %}
      {% assign last_content = "" %}

      <!-- Check for content before the first <h2> tag -->
      {% assign intro_content = content_sections[0] %}
      {% if intro_content != blank %}
        <!-- Remove empty <p> tags or spaces -->
        {% assign intro_content = intro_content | replace: "<p></p>", "" | strip %}
        {% if intro_content != "" %}
          {% assign last_content = intro_content %}
        {% endif %}
      {% endif %}

      <!-- Loop through the remaining sections split by <h2> tags -->
      {% for section in content_sections offset:1 %}
        {% if section contains "</h2>" %}
          {% assign section_parts = section | split: '</h2>' %}
          {% assign title = section_parts[0] | strip %}
          {% assign section_content = section_parts[1] | strip %}

          {% if title == "" %}
            <!-- If <h2> is blank, append content to the last accordion -->
            {% assign last_content = last_content | append: section_content %}
          {% else %}
            <!-- If we have a valid title, render the last accordion -->
            {% if last_content != "" %}
              <div class="accordion-item">
                <div class="accordion-title">
                  <h2>{{ last_title }}</h2>
                  <span class="plus" style="display: none;">+</span>
                  <span class="minus">-</span>
                </div>
                <div class="accordion-content active" style="display: block;">
                  {{ last_content | raw }}
                </div>
              </div>
            {% endif %}

            <!-- Update last_title and last_content -->
            {% assign last_title = title %}
            {% assign last_content = section_content %}
          {% endif %}
        {% else %}
          <!-- If no closing </h2>, append section to the last content -->
          {% assign last_content = last_content | append: section %}
        {% endif %}
      {% endfor %}

      <!-- Render the final accordion -->
      {% if last_content != "" %}
        <div class="accordion-item">
          <div class="accordion-title">
            <h2>{{ last_title }}</h2>
            <span class="plus" style="display: none;">+</span>
            <span class="minus">-</span>
          </div>
          <div class="accordion-content active" style="display: block;">
            {{ last_content | raw }}
          </div>
        </div>
      {% endif %}
    </div>
  </div>

{% else %}
  {% assign content = product.metafields.global.new_description.value %}
  {% if content != blank %}
    {% assign content_sections = content | split: '<h2>' %}

    <div class="accordion-wrapper">
      <div class="accordion-list">
        {% assign last_title = "Description" %}
        {% assign last_content = "" %}

        <!-- Check for content before the first <h2> tag in metafield content -->
        {% assign intro_content = content_sections[0] %}
        {% if intro_content != blank %}
          <!-- Remove empty <p> tags or spaces -->
          {% assign intro_content = intro_content | replace: "<p></p>", "" | strip %}
          {% if intro_content != "" %}
            {% assign last_content = intro_content %}
          {% endif %}
        {% endif %}

        <!-- Loop through the remaining sections split by <h2> tags -->
        {% for section in content_sections offset:1 %}
          {% if section contains "</h2>" %}
            {% assign section_parts = section | split: '</h2>' %}
            {% assign title = section_parts[0] | strip %}
            {% assign section_content = section_parts[1] | strip %}

            {% if title == "" %}
              <!-- If <h2> is blank, append content to the last accordion -->
              {% assign last_content = last_content | append: section_content %}
            {% else %}
              <!-- If we have a valid title, render the last accordion -->
              {% if last_content != "" %}
                <div class="accordion-item">
                  <div class="accordion-title">
                    <h2>{{ last_title }}</h2>
                    <span class="plus" style="display: none;">+</span>
                    <span class="minus">-</span>
                  </div>
                  <div class="accordion-content active" style="display: block;">
                    {{ last_content | raw }}
                  </div>
                </div>
              {% endif %}

              <!-- Update last_title and last_content -->
              {% assign last_title = title %}
              {% assign last_content = section_content %}
            {% endif %}
          {% else %}
            <!-- If no closing </h2>, append section to the last content -->
            {% assign last_content = last_content | append: section %}
          {% endif %}
        {% endfor %}

        <!-- Render the final accordion -->
        {% if last_content != "" %}
          <div class="accordion-item">
            <div class="accordion-title">
              <h2>{{ last_title }}</h2>
              <span class="plus" style="display: none;">+</span>
              <span class="minus">-</span>
            </div>
            <div class="accordion-content active" style="display: block;">
              {{ last_content | raw }}
            </div>
          </div>
        {% endif %}
      </div>
    </div>
  {% endif %}
{% endif %}
